mapper.select:
SELECT * FROM doctor_info


        AND department_id = #{departmentId}


        AND title = #{title}


        AND license_no = #{licenseNo}


        AND introduction LIKE CONCAT('%', #{introduction}, '%')


        AND doctor_name LIKE CONCAT('%', #{doctorName}, '%')


ORDER BY create_time DESC

mapper.insert:
INSERT INTO doctor_info (
    department_id, title, license_no, introduction, doctor_name
)
VALUES (
    #{departmentId}, #{title}, #{licenseNo}, #{introduction}, #{doctorName}
)

mapper.update:
UPDATE doctor_info
department_id = #{departmentId}, title = #{title}, license_no = #{licenseNo}, introduction = #{introduction}, doctor_name = #{doctorName},
WHERE doctor_id = #{doctorId}
