mapper.select:
SELECT * FROM doctor_schedule


        AND doctor_id = #{doctorId}


        AND schedule_type = #{scheduleType}


        AND schedule_date = #{scheduleDate}


        AND doctor_name LIKE CONCAT('%', #{doctorName}, '%')


        AND department = #{department}


ORDER BY schedule_date DESC

mapper.insert:
INSERT INTO doctor_schedule (
    doctor_id, schedule_type, schedule_date, time_range, max_patient,
    doctor_name, department
)
VALUES (
    #{doctorId}, #{scheduleType}, #{scheduleDate}, #{timeRange}, #{maxPatient},
    #{doctorName}, #{department}
)

mapper.update:
UPDATE doctor_schedule
schedule_type = #{scheduleType}, schedule_date = #{scheduleDate}, time_range = #{timeRange}, max_patient = #{maxPatient}, doctor_name = #{doctorName}, department = #{department},
WHERE schedule_id = #{scheduleId}
