mapper.select:
SELECT * FROM inpatient_record


        AND user_id = #{userId}


        AND doctor_id = #{doctorId}


        AND hospital_id = #{hospitalId}


        AND report_id = #{reportId}


ORDER BY create_time DESC

mapper.insert:
INSERT INTO inpatient_record (
    hospital_id, user_id, admission_diagnosis, discharge_summary, 
    doctor_id, report_id
)
VALUES (
    #{hospitalId}, #{userId}, #{admissionDiagnosis}, #{dischargeSummary}, 
    #{doctorId}, #{reportId}
)

mapper.update:
UPDATE inpatient_record
admission_diagnosis = #{admissionDiagnosis}, discharge_summary = #{dischargeSummary}, report_id = #{reportId},
WHERE record_id = #{recordId}
