mapper.select:
SELECT * FROM outpatient_record


        AND user_id = #{userId}


        AND doctor_id = #{doctorId}


        AND report_id = #{reportId}


ORDER BY create_time DESC

mapper.insert:
INSERT INTO outpatient_record (
    register_id, user_id, chief_complaint, diagnosis, 
    prescription, doctor_id, report_id
)
VALUES (
    #{registerId}, #{userId}, #{chiefComplaint}, #{diagnosis}, 
    #{prescription}, #{doctorId}, #{reportId}
)

mapper.update:
UPDATE outpatient_record
chief_complaint = #{chiefComplaint}, diagnosis = #{diagnosis}, prescription = #{prescription}, report_id = #{reportId},
WHERE record_id = #{recordId}
